import React, {useState, useEffect} from "react";
import './post.css';

const BASE_URL = "http://localhost:8000/"

function Post({post}) {
 const [imageUrl, setImageUrl] = useState('')

 useEffect(() => {
    setImageUrl(BASE_URL + post.imageUrl)
 }, [])

 return (
    <div className="post">
        <img className="post_image" src={imageUrl} alt="" />
    </div>
 )
}

export default Post
